function cssSlider(selector, sld_args){
	let n = 0
	window.setInterval(()=>{
		n++
		const len = sld_args.slide.length
		let img = sld_args.slide[n%len]
		document.querySelector(selector).style.backgroundImage = "url('" + img + "')";
	}, sld_args.delay)
}